  const searchData = (data)=>{
    axios.post('http://localhost:5003/bills/search', data)
            .then((response) => {
                setRecodes(response.data.data);
                setModalData({
                    partyName:""
                })
            })
  .catch((error) => {
    alert("Something Wrong",error)
  });
}
    const submitData = (data)=>{
        const token = localStorage.getItem('user');
        axios.post('http://www.localhost:5003/bills/addBill', data,{
            headers: {
              Authorization: `Bearer ${token}` // Prepend 'Bearer ' to the token
            }
          })
      .then((response) => {
        alert("Data Successfully Added", response.data);
        setModalData({
            PO_no:"",
            Line_Item_No:"",
            Challan_No:"",
            Vehicle_No:"",
            Material_Code1:"",
            Material_Code2:"",
            Material_Code3:"",
            Material_Code4:"",
            Material_Code5:"",
            Quantity1:"",
            Quantity2:"",
            Quantity3:"",
            Quantity4:"",
            Quantity5:""
          });
        setAddData(false)
        axios.get('http://localhost:5003/bills/getAllBills',null, {
            headers: {
              Authorization: `Bearer ${token}` // Prepend 'Bearer ' to the token
            }
          }).then(res => {
          setRecodes(res.data.data);
        });
        
      })
      .catch((error) => {
        alert("Something Wrong",error)
      });
  }

  const updateData = (data)=>{
    const token = localStorage.getItem('user');
    axios.post('http://www.localhost:5003/bills/updateBillById', data, {
        headers: {
          Authorization: `Bearer ${token}` // Prepend 'Bearer ' to the token
        }
      })
  .then((response) => {
    alert("Data Successfully Updated", response.data);
    setShowUpdate(false)
    axios.get('http://localhost:5003/bills/getAllBills',null, {
        headers: {
          Authorization: `Bearer ${token}` // Prepend 'Bearer ' to the token
        }
      }).then(res => {
      setRecodes(res.data.data);
    });
  })
  .catch((error) => {
    alert("Something Wrong",error)
  });
}
  const EditDetails = (_id)=>{
    axios.get(`http://localhost:5003/bills/getBillById?id=${_id}`).then((res)=>{
        setModalData(res.data.data)
    },[])
    setShowUpdate(true);
   
}

