                <p style="text-align: right;">Proprietor</p>
                <p style="text-align: left;">Receiver Sign.</p>
              </div>
            </div>
          </body>
          </html>
        `;
  
        // Load the QR code image and then generate the PDF
        const img = await loadImage(qrCodeUrl);
  
        // Create a temporary element to hold the HTML content
        const tempElement = document.createElement('div');
        tempElement.innerHTML = htmlContent;
        document.body.appendChild(tempElement);
  
        // Add the QR code image to the HTML content
        const qrCodeDiv = tempElement.querySelector('#qr-code');
        if (qrCodeDiv && img) {
          qrCodeDiv.appendChild(img);
        }
  
        // Use html2canvas to convert the HTML content to a canvas
        const canvas = await html2canvas(tempElement);
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF('p', 'mm', 'a4');
  
        // Calculate the width and height of the image to fit into the PDF
        const imgWidth = 210;
        const pageHeight = 295;
        const imgHeight = (canvas.height * imgWidth) / canvas.width;
        let heightLeft = imgHeight;
        let position = 0;
  
        // Add the image to the PDF
        pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
  
        // If the content is too long, add new pages
        while (heightLeft >= 0) {
          position = heightLeft - imgHeight;
          pdf.addPage();
          pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
          heightLeft -= pageHeight;
        }
  
        // Save the PDF
        pdf.save(`${billData.name || 'challan-document'}.pdf`);
  
        // Remove the temporary element from the DOM
        document.body.removeChild(tempElement);
      } catch (err) {
        console.error('Error:', err);
        alert('Failed to load QR code image or generate PDF.');
      }
    };
  


  
  
  

