                            <td style="border: 1px solid #ccc; border-right: none;"><td style="border: 1px solid #ccc; border-right: none;font-size: 10px;text-align:center"><h3>Total Amount</h3></td></td>
                            <td style="border: 1px solid #ccc; border-right: none;font-size: 10px;text-align:center">${billData.Total11}</td>
                        </tr>
                </tbody>
            </table>
                <hr>
                <div>
                    <p class="one">1- All Taxes (GST 18%) will Be Extra.</p>
                    <p class="one">2- Transportation/Travelling Charges Will Be Extra At Actual.</p>
                    <p class="one" style="color: red;">3- Payment 60% Advance & 40% After Delivery.</p>
                </div>
            </div>
            </body>
            </html>`;
            const iframe = document.createElement('iframe');
            iframe.style.display = 'none';
            document.body.appendChild(iframe);
            
            // Write content to the iframe
            const iframeDoc = iframe.contentWindow.document;
            iframeDoc.open();
            iframeDoc.write(htmlContent);
            iframeDoc.close();
          
            // Print the iframe content
            iframe.contentWindow.print();
          }
},[]);
  
  };

  const submitData = () => {
  
    // Update modalData with selected start and end dates
    const dataToSend = {
        startDate: selectedStartDate ? selectedStartDate.toISOString() : "", // Convert date to ISO string format
        endDate: selectedEndDate ? selectedEndDate.toISOString() : "",
        partyName:modalData.partyName
    };
console.log("333",dataToSend)
setModalData({
})
    // // Make sure selectedStartDate and selectedEndDate are not null before submitting
    if (selectedStartDate && selectedEndDate) {
        axios.post('http://localhost:5003/bills/getReportsBills', dataToSend)
            .then((response) => {
                setRecodes(response.data.data);
                setSelectedStartDate(null);
                setSelectedEndDate(null);
                setModalData({
                    partyName:""
                })
            })
            .catch((error) => {
                alert("Something Wrong", error);
            });
    } else {
        alert("Please select both start and end dates.");
    }
};


  return (
    <GridContainer>
      <GridItem xs={12} sm={12} md={12}>
        <Card plain>
          <CardHeader plain color="primary">
            <h4 className={classes.cardTitleWhite}>
                Bill Report
            </h4>
            <p className={classes.cardCategoryWhite}>
            </p>
          </CardHeader>
          <CardBody>
          <Form>
    <div style={{ display: 'flex', alignItems: 'center' }}>
        <Form.Group className="mb-3" style={{ marginRight: '10px' }}>
            <Form.Label style={{ fontWeight: 'bold' }}>Start Date:</Form.Label>
            <DatePicker
                className='form-control'
                selected={selectedStartDate}
                onChange={handleStartDateChange}
                dateFormat="yyyy-MM-dd"
            />
        </Form.Group>
        <Form.Group className="mb-3">
            <Form.Label style={{ fontWeight: 'bold' }}>End Date:</Form.Label>
            <DatePicker
                className='form-control'
                selected={selectedEndDate}
                onChange={handleEndDateChange}
                dateFormat="yyyy-MM-dd"
            />
        </Form.Group>
        <Form.Group className="mb-3 d-flex align-items-center">
            <Form.Label style={{ fontWeight: 'bold', margin: 0, marginRight: '10px' }}>Party Name:</Form.Label>
            <Form.Control 
                type="varchar"  
                name="partyName"  
                placeholder="" 
                value={modalData.partyName} 
                onChange={e => setModalData({ ...modalData, partyName: e.target.value })} 
                style={{ flex: 1 }} // Ensure the input takes remaining space
            />
            {/* Render search results */}
            {/* <ul>
                {searchResults.map(result => (
                    <li key={result.id}>{result.name}</li> 
                ))}
            </ul> */}
        </Form.Group>
        <Form.Group className="mb-3">
        <Button variant="primary" style={{marginLeft:"10px"}} onClick={()=>submitData(modalData)} >
                    Search
        </Button>
        </Form.Group>
    </div>
</Form>
          <Table
            tableHeaderColor="primary"
            tableHead={["ID","Invoice No","Party Name", "Number","Amount","Quotation Status","Created Date","Print"]}
            tableData={currentRecords.map((item, index) => ([
              index + 1,
              item.invoiceNumber,
              item.partyName,
              item.partyMobileNumber,
              item.Total11,
              item.billStatus,
              convertUTCToIST(item.createdAt),
              <Button variant="danger" key={`print-${index}`} onClick={() => handlePrint(item._id)}><FaPrint /></Button>
            ]))}
          />
                    <Pagination style={{ display: "flex", justifyContent: "center", marginBottom: "15px", marginTop: "5px"  }}>
            {Array.from({ length: Math.ceil(records.length / recordsPerPage) }).map(
            (item, index) => (
                <Pagination.Item
                key={index}
                active={index + 1 === currentPage}
                onClick={() => paginate(index + 1)}
                >
                {index + 1}
                </Pagination.Item>
            )
            )}
        </Pagination>
          </CardBody>
        </Card>
    
      </GridItem>
    </GridContainer>
  );
}
