        alert("Data Successfully Added", response.data);
        setModalData({
            PO_no:"",
            Line_Item_No:"",
            Challan_No:"",
            Vehicle_No:"",
            Material_Code1:"",
            Material_Code2:"",
            Material_Code3:"",
            Material_Code4:"",
            Material_Code5:"",
            Quantity1:"",
            Quantity2:"",
            Quantity3:"",
            Quantity4:"",
            Quantity5:""
          });
        setAddData(false)
        axios.post('http://localhost:5003/bills/getAllBills',null, {
            headers: {
              Authorization: `Bearer ${token}` // Prepend 'Bearer ' to the token
            }
          }).then(res => {
          setRecodes(res.data.data);
        });
        
      })
      .catch((error) => {
        alert("Something Wrong",error)
      });
  }

  const updateData = (data)=>{
    const token = localStorage.getItem('user');
    axios.post('http://www.localhost:5003/bills/updateBillById', data, {
