        Material_Code1:"",
        Material_Code2:"",
        Material_Code3:"",
        Material_Code4:"",
        Material_Code5:"",
        Quantity1:"",
        Quantity2:"",
        Quantity3:"",
        Quantity4:"",
        Quantity5:""
      });
    setAddData(true)
}
const handleClose = () => {
  setShowUpdate(false)
